import {useState} from "react";
import { Card, Button } from "react-bootstrap";
import { useNavigate } from "react-router-dom";

import { useParams } from "react-router-dom";
import styles from "./courseDetail.module.css";
const CourseDetail = () => {
  const { courseDetail,courseCategory } = useParams();
  const buttonHandle = () => {
    navigate("/courses/apply");
  };
    const navigate = useNavigate();
    const categories = [[ "kids", ["Weekday Evening Classes", "Weekend Morning Classes"], ["1-hr/day", "2-hr/day"], ["100€/month", "80€/month"] ], [{ category: "part-time", title: ["Weekday Evening Classes", "Weekend Morning Classes"], duration: ["3-hr/day", "6-hr/day"], fees: ["1000€/month", "800€/month"] }], [{ category: "full-time", title: ["Weekday Morning Classes"], duration: ["6-hr/day"], fees: ["1500€/month"] }]]

const [categoryIndex,setCategoryIndex]=useState(null)
    if (courseCategory === "kids") {
        setCategoryIndex(0)
    } else if (courseCategory === "part-time") {
         setCategoryIndex(0);
    }
  return (
    <div>
      <h2 className={styles.h2}>{courseDetail}</h2>
      <h2 className={styles.h2}>{courseCategory}</h2>
          {/* <LoadContent courseDetail={courseDetail} courseCategory={}/> */}

          {courseCategory === "kid" ? (
              categories[0][1].map((ele, i) => {

                 return <Card style={{ width: "18rem" }} key={i}>
                      {/* <Card.Img
                      variant="top"
                      src={pics[i]}
                      className={Styles.cardImg}
                    /> */}
                      <Card.Body>
                          <Card.Title>{courseDetail}</Card.Title>
                          <Card.Text>{ele}</Card.Text>
                          <Card.Text>Duration : {categories[0][2][i]}</Card.Text>
                          <Card.Text>Price: {categories[0][3][i]}</Card.Text>
                          <Button variant="info" onClick={buttonHandle}>
                              Apply Now
                          </Button>
                      </Card.Body>{" "}
                  </Card>

              })): null}
    </div>
  );
};

export default CourseDetail;
