import { Card, Button } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import { useParams } from "react-router-dom";
import styles from "./courseDetail.module.css";
import LoadContent from "./LoadContent.jsx"


  const buttonHandle = () => {
    navigate("/courses/apply");
  };
    const navigate = useNavigate();
    const categories = [[ kid, ["Weekday Evening Classes", "Weekend Morning Classes"], ["1-hr/day", "2-hr/day"], ["100€/month", "80€/month"] ], [ kidmom,["Weekday Evening Classes", "Weekend Morning Classes"],  ["3-hr/day", "6-hr/day"], ["1000€/month", "800€/month"] ], [man,  ["Weekday Morning Classes"], ["6-hr/day"], ["1500€/month"] ]]

    const [categoryIndex, setCategoryIndex] = useState(0)
    useEffect(() => {
      if (courseCategory === "kid") {
        setCategoryIndex(0)
    } else if (courseCategory === "part-time") {
         setCategoryIndex(1);
    } else if (courseCategory === "full-time") {
        setCategoryIndex(2)
    }

    }, [courseCategory])
console.log(categoryIndex)
    const CourseDetail = () => {
      return (
        <div>
          <h2 className={styles.h2}>{courseDetail}</h2>
          <h2 className={styles.h2}>{courseCategory}</h2>
          <LoadContent
            courseDetail={courseDetail}
            courseCategory={courseCategory}
          />
          {categoryIndex != null
            ? categories[categoryIndex][1].map((ele, i) => {
                return (
                  <Card style={{ width: "18rem" }} key={i}>
                    <Card.Img
                      variant="top"
                      src={categories[categoryIndex][0]}
                    />
                    <Card.Body>
                      <Card.Title>{courseDetail}</Card.Title>
                      <Card.Text>{ele}</Card.Text>
                      <Card.Text>
                        Duration : {categories[categoryIndex][2][i]}
                      </Card.Text>
                      <Card.Text>
                        Price: {categories[categoryIndex][3][i]}
                      </Card.Text>
                      <Button variant="info" onClick={buttonHandle}>
                        Apply Now
                      </Button>
                    </Card.Body>{" "}
                  </Card>
                );
              })
            : null}
        </div>
      );
    };

export default CourseDetail;
